import React from "react";

const MODAL_STYLES = {
    position: "fixed",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#fff",
    padding: "50px",
    zIndex: 1000
}


const Modal = ({open, children, handleDeleteClick}) => {
    return (
        <div className={MODAL_STYLES}>
            {children}
        </div>
    )
}

export default Modal;
