import React from "react";
import { ReactDOM } from "react";

const MODAL_STYLES = {
    position: "fixed",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#fff",
    padding: "50px",
    zIndex: 1000
}


const Modal = ({open, children, handleDeleteClick}) => {
    if (!open) return null

    return ReactDOM.createPortal(
        <>
            <div className={MODAL_STYLES}>
            {children}
        </div>,
        </>
    )
}

export default Modal;
