import React, { Fragment, useState } from "react";
import data from "../../mock-data.json";
import { EditData } from "../edit/EditData";
import ReadOnlyRow from "../readonlyRow/ReadOnlyRow";
import "./Todos.css"

const Todos = () => {
	const [contacts, setContacts] = useState(data);
	const [addFormData, setAddFormData] = useState({
		fullName: "",
		address: "",
		phoneNumber: "",
		email: "",
	});

	const [editContactId, setEditContactId] = useState(null);
	const [editFormData, setEditFormData] = useState({
		fullName: "",
		address: "",
		phoneNumber: "",
		email: "",
	})


	const handleAddFormChange = (e) => {
		const fieldName = e.target.getAttribute("name");
		const fieldValue = e.target.value;

		const newFormData = { ...addFormData };
		newFormData[fieldName] = fieldValue;

		setAddFormData(newFormData);
	};

	const handleAddFormSubmit = (e) => {
		e.preventDefault();

		const newContact = {
			id: Math.floor(Math.random() * 10000),
			fullName: addFormData.fullName,
			address: addFormData.address,
			phoneNumber: addFormData.phoneNumber,
			email: addFormData.email,
		}

		const newContacts = [...contacts, newContact];

		setContacts(newContacts)

		setAddFormData({
			fullName: "",
			address: "",
			phoneNumber: "",
			email: ""
		})

	};

	const handleEditClick = (event, contact) => {
		event.preventDefault();
		setEditContactId(contact.id)

		const formValues = {
			fullName: contact.fullName,
			address: contact.address,
			phoneNumber: contact.phoneNumber,
			email: contact.email
		}

		setEditFormData(formValues)
	};

	const handleEditFormChange = (event) => {
		const fieldName = event.target.getAttribute("name");
		const fieldValue = event.target.value;

		const newFormEditData = {...editFormData};
		newFormEditData[fieldName] = fieldValue;

		setEditFormData(newFormEditData)
	};

	const handlEditFormSubmit = (event) => {
		event.preventDefault();

		const editedContact = {
			id: editContactId,
			fullName: editFormData.fullName,
			address: editFormData.address,
			phoneNumber: editFormData.phoneNumber,
			email: editFormData.email
		}

		const newContact = [...contacts];

		const index = contacts.findIndex((contact) => contact.id === editContactId);

		newContacts[index] = editContactId
		setContacts
	};



	return (
		<>
			<h1>Add a Contact</h1>
			<form className="form" autoComplete="off" onSubmit={handleAddFormSubmit}>
				<input
					type="text"
					placeholder="Enter a name..."
					name="fullName"
					required
					onChange={handleAddFormChange}
					value={addFormData.fullName}
				/>
				<input
					type="text"
					placeholder="Enter an address..."
					name="address"
					required
					onChange={handleAddFormChange}
					value={addFormData.address}
				/>
				<input
					type="number"
					placeholder="Enter  a phone number..."
					name="phoneNumber"
					required
					onChange={handleAddFormChange}
					value={addFormData.phoneNumber}
				/>
				<input
					type="email"
					placeholder="Enter an email..."
					name="email"
					required
					onChange={handleAddFormChange}
					value={addFormData.email}
				/>
				<button className="addBtn">Add</button>
			</form>

			<input type="text" className="filter" placeholder="Search..." />


			<form className="form" autoComplete="off">
				<table className="styled-table">
					<thead>
						<tr>
							<th>N.</th>
							<th>Name</th>
							<th>Address</th>
							<th>Phone Number</th>
							<th>Email</th>
							<th>Actions</th>
						</tr>
					</thead>
					<tbody>
						{
							contacts.map((contact, index) => (
								<Fragment>
									{
										editContactId === contact.id ? (
											<EditData  
												index={index}
												editFormData={editFormData}
												handleEditFormChange={handleEditFormChange} 
											/>
										) : (

											<ReadOnlyRow 
												key={contact.id} 
												contact={contact} 
												index={index} 
												handleEditClick={handleEditClick}
											/>
										)
									}
								</Fragment>
							))
						}
					</tbody>
				</table>
			</form>
		</>
	);
};

export default Todos;
