import React, { Fragment, useState } from "react";
import data from "../../mock-data.json";
import EditableRow from "../edit/EditableRow";
import ReadOnlyRows from "../readOnlyRows/ReadOnlyRows";
import "./Todos.css"

const Todos = () => {
	const [contacts, setContacts] = useState(data);
	const [addFormData, setAddFormData] = useState({
		count: 1,
		fullName: "",
		address: "",
		phoneNumber: "",
		email: "",
	});

	const [editContactId, setEditContactId] = useState(3);

	const handleAddFormChange = (e) => {
		const fieldName = e.target.getAttribute("name");
		const fieldValue = e.target.value;

		const newFormData = { ...addFormData };
		newFormData[fieldName] = fieldValue;

		setAddFormData(newFormData);
	};

	const handleAddFormSubmit = (e) => {
		e.preventDefault();

		const newContact = {
			id: Math.floor(Math.random() * 10000),
			fullName: addFormData.fullName,
			address: addFormData.address,
			phoneNumber: addFormData.phoneNumber,
			email: addFormData.email,
		}

		const newContacts = [...contacts, newContact];

		setContacts(newContacts)

		setAddFormData({
			fullName: "",
			address: "",
			phoneNumber: "",
			email: ""
		})

	};

	const handleEditClick = (event, contact) => {
		event.preventDefault();
		setEditContactId(contact.id)
	};

	return (
		<>
			<h1>Add a Contact</h1>
			<form className="form" autoComplete="off" onSubmit={handleAddFormSubmit}>
				<input
					type="text"
					placeholder="Enter a name..."
					name="fullName"
					required
					onChange={handleAddFormChange}
					value={addFormData.fullName}
				/>
				<input
					type="text"
					placeholder="Enter an address..."
					name="address"
					required
					onChange={handleAddFormChange}
					value={addFormData.address}
				/>
				<input
					type="number"
					placeholder="Enter  a phone number..."
					name="phoneNumber"
					required
					onChange={handleAddFormChange}
					value={addFormData.phoneNumber}
				/>
				<input
					type="email"
					placeholder="Enter an email..."
					name="email"
					required
					onChange={handleAddFormChange}
					value={addFormData.email}
				/>
				<button className="addBtn">Add</button>
			</form>

			<input type="text" className="filter" placeholder="Search..." />


			<form>
				<table className="styled-table">
					<thead>
						<tr>
							<th>N.</th>
							<th>Name</th>
							<th>Address</th>
							<th>Phone Number</th>
							<th>Email</th>
							<th>Actions</th>
						</tr>
					</thead>
					<tbody>
						{
							contacts.map((contact, index) => (
								<Fragment>
									{
										editContactId === contact.id ? (
											<EditableRow />
										) : (
											<ReadOnlyRows 
											contact={contact} index={index} handleEditClick={handleEditClick} />
										)
									}
								</Fragment>
							))
						}
					</tbody>
				</table>
			</form>
		</>
	);
};

export default Todos;
