import React, { Fragment, useState } from "react";
import data from "../../mock-data.json";
import Edit from "../edit/Edit";
import ReadOnlyRow from "../readonlyRow/ReadOnlyRow";
import "./Todos.css"

const Todos = () => {
	const [contacts, setContacts] = useState(data);
	const [addFormData, setAddFormData] = useState({
		fullName: "",
		address: "",
		phoneNumber: "",
		email: ""
	});


	const [editId, setEditId] = useState(null);


	const handleAddFormChange = (event) => {
		const fieldName = event.target.getAttribute("name");
		const fieldValue = event.target.value;

		const newFormData = { ...addFormData };
		newFormData[fieldName] = fieldValue;

		setAddFormData(newFormData);
	};

	const handleAddFormSubmit = (event) => {
		event.preventDefault();

		const newContact = {
			id: Math.random(Math.floor() * 10000),
			fullName: addFormData.fullName,
			address: addFormData.address,
			phoneNumber: addFormData.phoneNumber,
			email: addFormData.email
		}

		const newContacts = [...contacts, newContact];
		setContacts(newContacts);

		setAddFormData({
			fullName: "",
			address: "",
			phoneNumber: "",
			email: ""
		})

	};

	return (
		<>
			<h1>Add a Contact</h1>
			<form className="form" autoComplete="off" onSubmit={handleAddFormSubmit}>
				<input
					type="text"
					placeholder="Enter a name..."
					name="fullName"
					required
					onChange={handleAddFormChange}
					value={addFormData.fullName}
				/>
				<input
					type="text"
					placeholder="Enter a address..."
					name="address"
					required
					onChange={handleAddFormChange}
					value={addFormData.address}
				/>
				<input
					type="number"
					placeholder="Enter a phone number..."
					name="phoneNumber"
					required
					onChange={handleAddFormChange}
					value={addFormData.phoneNumber}
				/>
				<input
					type="email"
					placeholder="Enter an email..."
					name="email"
					required
					onChange={handleAddFormChange}
					value={addFormData.email}
				/>
				<button className="addBtn">Add</button>
			</form>

			<input type="text" className="filter" placeholder="Search..." />

			<form>
				<table className="styled-table">
					<thead>
						<tr>
							<th>N.</th>
							<th>Fullname</th>
							<th>Address</th>
							<th>Phonenumber</th>
							<th>Email</th>
						</tr>
					</thead>
					<tbody>
						{
							contacts.map((contact, index) => (
								<Fragment>

									{
										editId === contact.id ? (
											<Edit />

										) : (
											<ReadOnlyRow key={contact.id} contact={contact} index={index} />

										)
									}
								</Fragment>
							))
						}
					</tbody>
				</table>
			</form>
		</>
	);
};

export default Todos;
